catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

    if (isLoading || newValue == '') {
        return;
    }

    var attachmentTable = 'incident';
    var attachmentId = g_form.getValue('add_attachments');
    var attachmentType = g_form.getValue('attachment_type');

	updateAttachmentInfo(attachmentTable, attachmentId, attachmentType);
    g_form.clearValue('add_attachments');

    function updateAttachmentInfo(attachmentTable, attachmentId, attachmentType) {

        var ga = new GlideAjax('AttachmentInfo');
        ga.addParam('sysparm_name', 'update');
        ga.addParam('sysparm_attachment_table', attachmentTable);
        ga.addParam('sysparm_attachment_id', attachmentId);
        ga.addParam('sysparm_attachment_type', attachmentType);
        ga.getXML(callback);

        function callback(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");
            alert(answer);
        }
    }

}
