sys_script_include.script:
var AttachmentInfo = Class.create();
AttachmentInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	update: function() {

		var attachmentTable = this.getParameter('sysparm_attachment_table');
        var attachmentId = this.getParameter('sysparm_attachment_id');
		var attachmentType = this.getParameter('sysparm_attachment_type');

		var attachment = new GlideRecord('sys_attachment');
		if(attachment.get(attachmentId)) {
			attachment.setValue('table_name', attachmentTable);
			attachment.update();
		}

		var attachmentInfo = new GlideRecord('x_1699952_attach_0_attachment_info');
		attachmentInfo.initialize();
		attachmentInfo.setValue('u_attachment', attachment.getUniqueValue());
		attachmentInfo.setValue('u_tag', attachmentType);
		attachmentInfo.insert();

		var response = {};
		response.attachmentTable = attachmentTable;
		response.attachmentId = attachmentId;
		response.attachmentType = attachmentType;

        return JSON.stringify(response);
    },

    type: 'AttachmentInfo'
});
